import React from "react";
import CarCard from "../Components/CarCard";
import Fade from 'react-reveal/Fade';
import elbiler from '../elbiler.json';
import styled from '@emotion/styled';
import { FaMapMarkerAlt, FaCoins, FaArrowsAltV } from 'react-icons/fa';

const Footer = styled.footer`
    position: fixed;
    display: flex;
    width: 100vw;
    box-sizing: border-box;
    box-shadow: 0px 0px 5px 0px rgba(0,0,0,0.75);
    bottom: 0;
    left: 0;
    height: 60px;
    background: #fff;        
`

const Footerinner = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    width: 100%;


    button{
      background: none; 
      border: none;
      margin: .5rem;
      outline: none;
      font-size: .7rem;
      font-weight: 200;
      color: #2d2d2d;

      svg{
        width: 20px;
        height: 20px;
        margin-bottom: .3rem;
      }
    }
`

const FlexWrapper = styled.div`
    background: none;
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin: .5rem;
    padding-bottom: 3rem;
    align-content: center;
    align-items: center;
    @media (min-width: 600px) {
      flex-direction: row;
      justify-content: center;
      flex-wrap: wrap;
    }

    .react-reveal{
      width: 100%;
      @media (min-width: 600px) {
        width: 30vw;
        margin: 1rem;
      }
  }`

class Car extends React.Component {
  constructor(props) {
    super(props);

    this.state = { 
      elbiler: elbiler,
      elbilDefault: [...elbiler],
      orderByRange: 'asc',
      orderByPrice: 'asc',
      orderBySize:  'asc'
    };
  }

  // Have to change from string to number  in json for it to work
  sortBySize = () => {
    let elbilerSize = [...this.state.elbiler]
    elbilerSize.sort(function(a, b) {
      return a.storelse - b.storelse
    })

    if(this.state.orderByRange === "desc") {
      elbilerSize.reverse() 
    }

    this.setState({elbiler: elbilerSize})
  }

  sortByRange = () => {
    let elbilerCopy = [...this.state.elbiler]
    elbilerCopy.sort(function(a, b) {
      return a.rekkevidde - b.rekkevidde;
    })

    if(this.state.orderByRange === "desc") {
      elbilerCopy.reverse() 
    }

    this.setState({elbiler: elbilerCopy});
  }

  sortByPrice = () => {
    let elbilerCopy = [...this.state.elbiler]
    elbilerCopy.sort(function(a, b) {
      return a.pris - b.pris
    })

    if(this.state.orderByPrice === "desc") {
      elbilerCopy.reverse() 
    }

    this.setState({elbiler: elbilerCopy});
  }

  reset = () =>{
    this.setState({elbiler: this.state.elbilDefault});
  }


  shuffle(array) {
    var currentIndex = array.length, temporaryValue, randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;

      // And swap it with the current element.
      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }

    return array;
  }

  render() {
    const shuffleElbiler = this.shuffle(this.state.elbiler)
    const elBiler = this.state.elbiler.map(elbil => 

    <Fade>
      <CarCard 
          merke={elbil.modell} 
          modell={elbil.merke}  
          rekkevidde={elbil.rekkevidde} 
          pris={elbil.pris} 
          bildeURL={elbil.bildeURL}
          type={elbil.type}
          id={elbil.id}
      /> 
    </Fade>
    );

    return (
      <FlexWrapper>
        {elBiler}
        <Footer>
          <Footerinner>
              <button onClick={() => {
                this.setState({
                  orderByRange: (this.state.orderByRange === 'asc' ? 'desc' : 'asc')
                }, () => {
                  this.sortByRange();
                })
              }}>
                <FaMapMarkerAlt  /><br />Rekkevidde
              </button>

              <button onClick={() => {
                this.setState({
                  orderByPrice: (this.state.orderByPrice === 'asc' ? 'desc' : 'asc')
                }, () => {
                  this.sortByPrice();
                })
              }}><FaCoins  /><br />Pris</button>


              <button onClick={() => {
                this.setState({
                  orderBySize: (this.state.orderBySize === 'asc' ? 'desc' : 'asc')
                }, () => {
                  this.sortBySize();
                })
              }}><FaArrowsAltV  /> <br />Størrelse</button>

              <button onClick={this.reset}>Reset</button>
          </Footerinner>
        </Footer>
      </FlexWrapper>
    );
  }
}

export default Car;
